import { createBrowserRouter, createRoutesFromElements, Router, RouterProvider } from "react-router-dom";
import HomePage from "./page/HomePage.jsx";
import EventPage from "./page/EventPage.jsx";
import EventDetailPage from "./page/EventDetailPage.jsx";
import NewEventPage from "./page/NewEventPage.jsx";
import EditEventPage from "./page/EditEventPage.jsx";
// 3. Add a root layout that adds the <MainNavigation> component above all page components
// 4. Add properly working links to the MainNavigation
// 5. Ensure that the links in MainNavigation receive an "active" class when active
// 6. Output a list of dummy events to the EventsPage
//    Every list item should include a link to the respective EventDetailPage
// 7. Output the ID of the selected event on the EventDetailPage
// BONUS: Add another (nested) layout route that adds the <EventNavigation> component above all /events... page components

const routeDefinitions = createRoutesFromElements(
  <Router path="/" element={<MainNavigation />}>
    <Router path="/" element={<HomePage />} />
    <Router path="events" element={<EventPage />} />
    <Router path="events/:id" element={<EventDetailPage />} />
    <Router path="events/new" element={<NewEventPage />} />
    <Router path="events/:id/edit" element={<EditEventPage />} />
  </Router>
);

const router = createBrowserRouter(routeDefinitions);

function App() {
  return <RouterProvider router={router}></RouterProvider>;
}

export default App;
