import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    addItemToCart(state, action) {
      const newItem = action.payload;
      const existingItem = state.items.find((item) => item.id === newItem.id);
      state.totalQuantity++;
      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          name: newItem.name,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price,
        });
      } else {
        existingItem.quantity++;
        console.log(existingItem.totalPrice);
        console.log(newItem);
        existingItem.totalPrice += newItem.price;
      }
    },
    removeItemFromCart(state, action) {
      const id = action.payload;
      const existingItem = state.items.find((item) => item.id === id);
      state.totalQuantity--;
      if (existingItem.quantity === 1) {
        state.items = state.items.filter((item) => item.id !== id);
      } else {
        existingItem.quantity--;
        existingItem.totalPrice -= existingItem.price;
      }
    },
  },
});

export function sendCartData() {
  return async (dispatch) => {
    dispatch(
      uiActions.showNotification({
        status: "pending",
        title: "sending...",
        message: "sending the data to the server",
      })
    );

    async function sendRequest() {
      const response = await fetch("https://redux-14edc-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json", { method: "PUT", body: JSON.stringify(cart) });

      if (!response.ok) {
        throw new Error("Failed fetched the data");
      }

      const resData = response.json();

      dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Success!!",
          message: "Data succesfully sent to the server",
        })
      );
    }

    try {
      await sendRequest()
      
    } catch (error) {
      
    }
  };
}

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
