import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: { 
    items: [],
    totalQuantitiy: 0,

  },
  reducers: {
    addToCart(state) {},
    increment(state) {},
    decodeURIComponent(state) {},
  },
});

export const cartAction = cartSlice.actions;

export default cartSlice.reducer;
